.sample_container {
  max-width: 300px;
  margin: auto;
  min-height: 400px;
}

/* Card Customizations */

.e-custom-card {
  position: relative;
  margin-top: 80px;
  overflow: visible;
  border-radius: 15px;
  box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
  transition: 0.2s;
  border-width: 1px;
  padding: 1px;
}

.e-custom-card.e-card:hover {
  border-width: 1px;
  padding: 1px;
}

.e-custom-card :nth-child(2) .e-card-header-title.name {
  margin-top: 20px;
}

.sample_container p.avatar-content {
  line-height: 20px;
  font-family: inherit;
}

.e-custom-card .e-card-header {
  text-align: center;
}

.e-custom-card .e-avatar {
  font-size: 40px;
  position: absolute;
  top: calc(0% - 1.5em);
  left: calc(50% - 1.5em);
  box-shadow: 0 16px 28px -8px rgba(0, 0, 0, .36), 0 4px 15px 0 rgba(0, 0, 0, .12), 0 8px 10px -5px rgba(0, 0, 0, .2);
}

