            ]
        },
        {
            "blockType": "Quote",
            "content": [
                {
                    "contentType": "Text",
                    "content": "‚ÄúEvery interaction tells a story ‚Äî listen carefully.‚Äù",
                    "properties": {
                        "styles": {
