/**
 * ComboBox Defaut functionality Sample
 */
import { Component, ViewChild, NgModule, ViewEncapsulation } from '@angular/core';
import { ComboBoxComponent, VirtualScroll, ComboBoxModule } from '@syncfusion/ej2-angular-dropdowns';
import { Query, DataManager, UrlAdaptor } from '@syncfusion/ej2-data';



ComboBoxComponent.Inject(VirtualScroll);

@Component({
    selector: 'app-root',
    templateUrl: 'app.component.html',
    styleUrls: ['app.component.css'],
    encapsulation: ViewEncapsulation.None,
    standalone: true,
    imports: [ ComboBoxModule, ]
})
export class AppComponent {
    @ViewChild('sample1')
    public localComboBoxObj: ComboBoxComponent;
    @ViewChild('sample2')
    public remoteComboBoxObj: ComboBoxComponent;
    @ViewChild('sample3')
    public groupComboBoxObj: ComboBoxComponent;
    // defined the array of data
    public records: { [key: string]: Object }[] = [];
    constructor() {
        for (let i = 1; i <= 150; i++) {
            let item: { [key: string]: Object } = {};
            item.id = 'id' + i;
            item.text = `Item ${i}`;

            // Generate a random number between 1 and 4 to determine the group
            const randomGroup = Math.floor(Math.random() * 4) + 1;
            switch (randomGroup) {
                case 1:
                    item.group = 'Group A';
                    break;
                case 2:
                    item.group = 'Group B';
                    break;
                case 3:
                    item.group = 'Group C';
                    break;
                case 4:
                    item.group = 'Group D';
                    break;
                default:
                    break;
            }
            this.records.push(item);
        }
    }

    // bind the DataManager instance to dataSource property
    public customerData: DataManager = new DataManager({
        url: 'https://services.syncfusion.com/js/production/api/VirtualDropdownData',
        adaptor: new UrlAdaptor,
        crossDomain: true
    });
    // maps the appropriate column to fields property
    public fields: { [key: string]: string } = { text: 'text', value: 'id' };
    public customerField: { [key: string]: string } = { text: 'OrderID', value: 'OrderID' };
    public groupField: { [key: string]: string } = { groupBy: 'group', text: 'text', value: 'id' };
    public waterMark: string = 'e.g. Item 1';
    public customerWaterMark: string = 'OrderId';
}

