.sb-mobile-palette {
    width:240px;
    height:100%;
    float:left;
}

.sb-mobile-palette-bar {
    display: none;
}

.sb-mobile-diagram {
    width:calc(100% - 242px);
    height: 100%;
    float: left;
}

@media (max-width: 550px) {

    .sb-mobile-palette {
        z-index: 19;
        position: absolute;
        display: none;
        transition: transform 300ms linear, visibility 0s linear 300ms;
        width:39%;
        height:100%;
    }

    .sb-mobile-palette-bar {
        display: block;
        width: 100%;
        background:#fafafa;
        padding: 10px 10px;
        border:0.5px solid #e0e0e0;
        min-height: 40px;
    }

    .sb-mobile-diagram {
        width: 100%;
        height: 100%;
        float: left;
        left: 0px;
    }

    #palette-icon {
        font-size: 20px; 
    }
}

.sb-mobile-palette-open {
    position: absolute;
    display: block;
    right: 15px;
}

