        document.getElementById('uploadfileButton').setAttribute('accept', '.doc,.docx,.rtf,.txt,.htm,.html');
        this.updateContainerSize();
        this.documentEditor.resize();
        this.onLoadDefault();
        this.applyPageCountAndDocumentTitle();
