        { Name: 'Nancy', Eimg: '4'},
        { Name: 'Robert', Eimg: '8'},
        { Name: 'Steven', Eimg: '10'}
    ];
    // maps the appropriate column to fields property
    public fields: Object = { text: 'Name'};
