/* ListView position alignment */

#groupedList.e-listview {
    display: block;
    max-width: 350px;
    margin: auto;
    border: 1px solid;
    line-height: initial;
    border-color: rgba(0, 0, 0, 0.12);
}

/* ListView header alignment */

#groupedList.e-listview .e-headertext:after {
    font-family: 'e-group-list-icons';
