 * Print sample
 */
import { Component, ViewEncapsulation, ViewChild, Inject } from '@angular/core';
import { MapsTheme, Maps, Legend, MapsTooltip, ILoadEventArgs } from '@syncfusion/ej2-angular-maps';
import { MapAjax } from '@syncfusion/ej2-maps';
Maps.Inject( MapsTooltip, Legend);
declare var require: any;
let usMap: object[] = require('./../../../../src/app/maps/usa.json');
let population: object[] = require('./../../../../src/app/maps/us-population.json');
@Component({
    selector: 'app-root',
    templateUrl: 'app.component.html',
    encapsulation: ViewEncapsulation.None
})
export class AppComponent {
    @ViewChild('maps')
    public maps: Maps;
    // custom code start
    public load = (args: ILoadEventArgs) => {
        let theme: string = location.hash.split('/')[1];
        theme = theme ? theme : 'Material';
        args.maps.theme = <MapsTheme>(theme.charAt(0).toUpperCase() + theme.slice(1));
    }
    // custom code end
    public zoomSettings: object= {
        enable: false
    };
    public legendSettings: object = {
            visible: true,
            mode: 'Interactive',
            position: 'Bottom',
            height: '10',
            width: '350',
            labelDisplayMode: 'Trim',
            alignment: 'Center'
        };
    public titleSettings: object = {
        text: 'State-wise US population - 2010',
        titleStyle: { size: '16px' },
    };

    public layers: object[] =  [
        {
            shapeData: usMap,
            shapeDataPath: 'name',
            shapePropertyPath: 'name',
            dataSource: population,
               shapeSettings: {
                border: {
                    width: 0.5,
                    color: 'gray'
                },
                colorValuePath: 'population',
                colorMapping: [
                    {
                        from: 580000 , to: 2800000, color: '#dae8f1', label: '<3M'
                    },
