    url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.em-icons { 
    font-family: 'em-icons';
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    line-height: 1;
    text-transform: none;
}
