/**
* ej2 Menu styles customization
*/
.deviceLayout .e-menu {
    -ms-overflow-style: none;
    scrollbar-width: none;
    height: 363px;
}
.deviceLayout .e-menu::-webkit-scrollbar {
    width: 0;
}

/**
* ej2 Menu styles
*/
.menu-control {
    text-align: center;
}

#layoutcontainer:not(.deviceLayout) {
    margin-top: 45px;
}

.deviceLayout {
    line-height: initial;
    border: 1px solid black;
    width: 285px;
    height: 505px;
    margin: auto;
    margin-bottom: 15px;
    border-radius: 28px;
    position: relative;
    background-image: linear-gradient(to top, #ffffff, #f5f5f5);
}

.fluent-dark .e-menu-wrapper.e-hamburger.e-lib.e-keyboard {
    background-color: black;
}

.fluent-dark .e-menu-wrapper.e-lib.e-keyboard {
    background-color: black;
}

.deviceLayout.tabletview {
    width: 767px;
    z-index: 1;
}

.deviceLayout .speaker {
    border: 1px solid black;
    border-radius: 5px;
    width: 20%;
    height: 5px;
    margin: 15px auto 0px auto;
    position: relative;
}

.deviceLayout .outerButton {
    width: 30px;
    height: 30px;
    border: 1px solid black;
    border-radius: 50%;
    position: absolute;
    bottom: calc(0% + 10px);
    left: calc(50% - 15px);
}

.deviceLayout .camera {
    position: absolute;
    left: calc(-15% - 10px);
    top: -100%;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    border: 1px solid black;
}

.deviceLayout .layout {
    border: 1px solid black;
    margin: 20px 13px 0px 13px;
}

.layout #container {
    height: 405px;
    background-color: white;
    overflow: hidden;
}

#header {
    width: 100%;
    background-color: #7b8cfb;
}

.bootstrap5-dark .layout #container,
.fluent-dark .layout #container,
.material-dark .layout #container,
.bootstrap5\.3-dark .layout #container,
.fluent2-dark .layout #container,
.fluent2-highcontrast .layout #container,
.material3-dark .layout #container,
.bootstrap-dark .layout #container,
.fluent-dark .layout #container,
.tailwind-dark .layout #container,
.highcontrast-dark .layout #container,
.highcontrast .layout #container {
    background-color: black;
}

/**
* ej2 Menu Tailwind 3 styles
*/
.tailwind3-dark .layout #container {
    background: #111827;
}
.tailwind3-dark .deviceLayout {
    border-color: #374151;
    background-image: none;
}
.tailwind3-dark .deviceLayout .layout {
    border-color: #374151;
}
.tailwind3-dark .deviceLayout .camera,
.tailwind3 .deviceLayout .camera {
    display: none;
}
.tailwind3-dark .deviceLayout .speaker,
.tailwind3-dark .deviceLayout .outerButton {
    border-color: #282F3C;
    background: #282F3C;
}
.tailwind3 .deviceLayout {
    border-color: #D1D5DB;
    background-image: none;
}
.tailwind3 .deviceLayout .layout {
    border-color: #D1D5DB;
}
.tailwind3 .deviceLayout .speaker,
.tailwind3 .deviceLayout .outerButton {
    border-color: #E5E7EB;
    background: #E5E7EB;
}
