.col-lg-8.control-section.otp-wrapper {
    display: flex;
    align-items: center;
    justify-content: center;
}

.col-lg-4.property-section.otp-property-panel {
    border-left: 1px solid #D7D7D7;
}

.sb-property-border.otp-wrapper {
    border: none;
}

#otp-container {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 50%;
}

.form-container {
    height: 220px;
    padding: 1rem 3rem;
    box-shadow: 0 1px 3px #d4d4d5, 0 0 0 1px #d4d4d5;
    border-radius: 4px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

.form-container>div {
    margin: 17px 0px;
}

.form-container .otp-header {
    display: inline-block;
    font-size: 1.4em;
    font-weight: 500;
    margin: 10px 0px;
}

.form-container .otp-actions {
    display: flex;
    gap: 20px;
}

.otp-actions button {
    width: 80px;
}

.property-panel-content td {
    padding: 10px 0px;
    width: 35%;
}

@media (max-width: 1200px) {
    .col-lg-4.property-section.otp-property-panel {
        border: none;
    }
}
