@Component({
    standalone: true,
    imports: [DropDownListModule, RadioButtonModule, CheckBoxModule,  ButtonModule, DialogModule, RadioButtonModule, ChartAllModule, RangeNavigatorAllModule, AccumulationChartAllModule, GridAllModule, RichTextEditorAllModule, ScheduleAllModule, RecurrenceEditorAllModule, CheckBoxModule, FormsModule, ReactiveFormsModule, CommonModule,  TabAllModule, DatePickerModule],
