#template-rating-control .rating-content {
    width: 240px;
    margin: 15px 0;
    min-height: 75px;
    padding:0;
    text-align: center;
}
/* Represents the styles in touch mode */
.e-bigger #template-rating-control .rating-content {
    width: 260px;
    margin: 15px 0;
    min-height: 75px;
    padding:0;
    text-align: center;
}
#template-rating-control {
    margin: 6% auto;
    max-width: 600px;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}

#template-rating-control .e-rating-container .custom-font {
    /* To add the background color for the font icon. */
    background: linear-gradient(to right, rgb(254,87,133,255) var(--rating-value), transparent var(--rating-value));
    /* To clip the background to the icon (text) alone. */
    background-clip: text;
    -webkit-background-clip: text;
    /* To make the background color visible instead of font color. */
    -webkit-text-fill-color: transparent;
    /* To provide a border for font icon. */
    -webkit-text-stroke: 1px rgb(254,87,133,255)
}

#template-rating-control .custom-icon .e-icons::before{
    content: "\e700";
    font-family: 'sb_rating' !important;
    speak: none;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
}

#template-rating-control .custom-svg .e-rating-item-container{
    padding: 0px;
}

