#simplified-pictureList {
  border: 1px;
  box-shadow: 0 0 2px;
  display: none;
}

#pictureddl-popup #simplified-pictureList {
  display: block;
}

.e-bigger #simplified-pictureList .e-list-header {
  background-color: white;
 }

 body[class*="dark"].e-bigger #simplified-pictureList .e-list-header,
 body[class*="high"].e-bigger #simplified-pictureList .e-list-header {
     background-color: #212529;
 }

 .e-bigger #simplified-pictureList ul {
     padding: 0px;
     margin-top: 0px;
 }

#simplified-ribbonContainer{
  padding: 20px;
  background-color: #dbdadacc;
  display: flex;
  flex-direction: column;
  height: 550px;
}

#simplified-ribbon {
  box-sizing: border-box;
}

#simplified-ribbonPlaceHolder {
  flex-grow: 1; 
  box-sizing: border-box;
  background-color: transparent;
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 15px;
}

/* content customization */
#simplified-ribbonPlaceHolder .content1 {
  background-color: #3a9ae5;
  width: 150px;
  height: 60%;
}

#simplified-ribbonPlaceHolder .content2 {
  background-color: #9d9c9a;
  width: calc(100% - 160px);
  height: 60%;
}

#simplified-ribbonPlaceHolder .content3 {
  background-color: #7f7e7d;
  width: 150px;
  height: calc(40% - 10px);
}

#simplified-ribbonPlaceHolder .content4 {
  background-color: #c8c6c4;
  width: calc(100% - 160px);
  height: calc(40% - 10px);
}

#simplified-ribbonPlaceHolder #toast .e-toast-close-icon {
  position: absolute;
  top: 50%;
  right: 2px ;
  transform: translateY(-50%);
  margin: 0;
}

#simplified-ribbonPlaceHolder #toast .e-toast-content {
  padding-right: 25px;
}

body[class*="dark"] #ribbonContainer,
body[class*="high"] #ribbonContainer {
  background: #a7a7a742;
}

.font-group .e-ribbon-group-content {
  justify-content: center;
}

#simplified-ribbonContainer #header_footer .e-ribbon-group-overflow-ddb {
  min-width: 10ch;
}

/* Represents the styles for icons */
@font-face {
font-family: 'ribbon';
src:
url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
font-weight: normal;
font-style: normal;
}

[class^="sf-icon-"], [class*=" sf-icon-"] {
font-family: 'ribbon' !important;
speak: none;
