app.component.css:
#splitter .e-avatar.e-avatar-xlarge {
    font-size: 22px;
}

.header-image {
    text-align: center;
    margin-top: 7px;
}

.e-custom-card .e-avatar {
    font-size: 40px;
    position: absolute;
    top: calc(0% - 1.5em);
    left: calc(50% - 1.5em);
    box-shadow: 0 16px 28px -8px rgba(0, 0, 0, .36), 0 4px 15px 0 rgba(0, 0, 0, .12), 0 8px 10px -5px rgba(0, 0, 0, .2);
}

/* ListView template customization */

#ui-list.e-listview .showUI {
    display: flex;
}

#ui-list.e-listview .hideUI {
    display: none;
}

#ui-list.e-listview .e-list-item {
    padding: 3px 0;
}

#ui-list.e-listview .R {
    background: lightgrey;
}

#ui-list.e-listview .M {
    background: lightblue;
}

.highcontrast #ui-list.e-listview .e-list-item.e-active {
    background: #ffd939;
    color: #000000;
}

.details-view table {
    border-collapse: collapse;
    border-spacing: 0;
    width: calc(100% - 20px);
    margin: 10px;
}

.details-view table th,
.details-view table td {
    font-weight: normal;
    padding: 5px;
    text-align: left;
    border: 1px solid #ddd
}

.e-bold {
    font-weight: 550;
}

.profile-name {
    font-weight: 550;
    font-size: 14px;
    text-align: center;
}


.control-section {
    overflow: auto;
}

/* Listview Customization */

#letterAvatarList {
    max-width: 350px;
    margin: auto;
    border-radius: 3px;
}

#letterAvatarList .listWrapper {
    width: inherit;
    height: inherit;
    position: relative;
}

#letterAvatarList .e-list-header {
    height: 54px;
}

.material #letterAvatarList .e-list-header .e-text {
    line-height: 22px;
}

.fabric #letterAvatarList .e-list-header .e-text {
    line-height: 22px;
}

.bootstrap #letterAvatarList .e-list-header .e-text {
    line-height: 13px;
}

.highcontrast #letterAvatarList .e-list-header .e-text {
    line-height: 20px;
}

#letterAvatarList .e-list-item {
    cursor: pointer;
    height: 50px;
    line-height: 44px;
    border: 0;
}

/* Badge Positioning */

#letterAvatarList .e-badge {
    margin-top: 12px;
}

#letterAvatarList .listWrapper .list-text {
    width: 60%;
    display: inline-block;
    vertical-align: middle;
    margin: 0 50px;
}

/* Avatar Positioning */

#letterAvatarList .listWrapper .e-avatar {
    position: absolute;
    top: calc(100% - 40px);
    font-size: 10px;
    left: 5px;
}

.material3 #letterAvatarList .listWrapper .e-avatar,
.material3-dark #letterAvatarList .listWrapper .e-avatar,
.tailwind #letterAvatarList .listWrapper .e-avatar,
.tailwind3 #letterAvatarList .listWrapper .e-avatar,
.tailwind-dark #letterAvatarList .listWrapper .e-avatar,
.tailwind3-dark #letterAvatarList .listWrapper .e-avatar {
    top: calc(100% - 45px);
}

.e-bigger.material3 #letterAvatarList .listWrapper .e-avatar,
.e-bigger.material3-dark #letterAvatarList .listWrapper .e-avatar,
.e-bigger.tailwind #letterAvatarList .listWrapper .e-avatar,
.e-bigger.tailwind3 #letterAvatarList .listWrapper .e-avatar,
.e-bigger.tailwind-dark #letterAvatarList .listWrapper .e-avatar,
.e-bigger.tailwind3-dark #letterAvatarList .listWrapper .e-avatar {
    top: calc(100% - 48px);
}

/* Avatar Background Customization */

#letterAvatarList .blue {
    background-color: #039BE5;
}

#letterAvatarList .red {
    background-color: #E91E63;
}

#letterAvatarList .green {
    background-color: #009688;
}

/* Avatar images using 'background-image' property */

.pic01 {
    background-image: url('https://ej2.syncfusion.com/demos/src/splitter/images/margaret.png');
}

.pic02 {
    background-image: url('https://ej2.syncfusion.com/demos/src/splitter/images/laura.png');
}

.pic03 {
    background-image: url('https://ej2.syncfusion.com/demos/src/splitter/images/robert.png');
}

.pic04 {
    background-image: url('https://ej2.syncfusion.com/demos/src/splitter/images/albert.png');
}

.pic05 {
    background-image: url('https://ej2.syncfusion.com/demos/src/splitter/images/michale.png');
}


